        `cover` TEXT NOT NULL,
        `role_icon` TEXT NULL,
        `about` TEXT NULL,
        `social` TEXT NULL,
        `role` varchar(32) NOT NULL,
        `country` varchar(48) NULL,
        `age` varchar(32) NULL,
        `stats` TEXT NULL,
        `equipment` TEXT NULL,
        `orderNum` int(11) NULL DEFAULT NULL,
        `created_at` DATETIME NOT NULL
    )" . $wpdb->get_charset_collate() . ";");

